mapper.select:
SELECT 

FROM t_tm_scene
WHERE center_lng <= #{maxLng} 
  AND center_lng >= #{minLng}
  AND center_lat <= #{maxLat}
  AND center_lat >= #{minLat}

SELECT 
COUNT(1)
FROM t_tm_scene
WHERE center_lng <= #{maxLng} 
  AND center_lng >= #{minLng}
  AND center_lat <= #{maxLat}
  AND center_lat >= #{minLat}

SELECT 

FROM t_tm_scene
WHERE name like #{name}

		select 	city as "city",
				avg(left_bottom_lat) as "leftBottomLat",
        		avg(left_bottom_lng) as "leftBottomLng",
       	 		count(1) as "count" 
		 from t_tm_scene 
		where center_lng <= #{maxLng} 
		  AND center_lng >= #{minLng}
		  AND center_lat <= #{maxLat}
		  AND center_lat >= #{minLat}
		group by city
